sp_widget.client_script:
api.controller=function($scope, $rootScope) {
	/* widget controller */
	var c = this;

	$scope.positionSelection = {
		displayValue: '',
		value: '',
		name: 'position'
	};

	$scope.skillSelection = {
		displayValue: '',
		value: '',
		name: 'skill'
	};

	$scope.startDateSelection = '';
	$scope.typeSelection = '';

	// Type
	$('#typeSelect').select2({
		placeholder: 'Type'
	});

	// Submit filters to Browse Positions
	$scope.submitFilters = function () {
		$scope.typeSelection = $('#typeSelect').select2('data');

		var filterObj = {
			position: $scope.positionSelection,
			skill: $scope.skillSelection,
			startDate: $scope.startDateSelection,
			type: $scope.typeSelection
		};

		//console.log(filterObj);

		$rootScope.$broadcast("submit-filters", filterObj);
	}

};

